mapper.insert:
declare
begin
insert into SYS_NAVIGATION_T ( ITEM_ID, ITEM_NAME, PARENT_ID, ICON, URL, RESOURCE_TYPE, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE ) values ( SYS_NAVIGATION_S.NEXTVAL, #{item.itemName,jdbcType=VARCHAR}, #{item.parentId,jdbcType=NUMERIC}, #{item.icon,jdbcType=VARCHAR}, #{item.url,jdbcType=VARCHAR}, #{item.resourceType,jdbcType=INTEGER}, #{item.orderCode,jdbcType=NUMERIC}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE );
end;

mapper.select:

WHERE 1 = 1

	AND T1.PARENT_ID = #{parentId,jdbcType=NUMERIC}

ORDER BY T1.ORDER_CODE ASC
