mapper.select:
SELECT

FROM SYS_LANGUAGE_T T1
where VALID_FLAG=1 AND T1.LAN_ID = #{lanId,jdbcType=NUMERIC}

mapper.update:
declare
begin
UPDATE SYS_LANGUAGE_T SET REG_NAME = #{item.regName,jdbcType=VARCHAR}, REG_CODE = #{item.regCode,jdbcType=VARCHAR}, REG_DESC = #{item.regDesc,jdbcType=VARCHAR}, PARENT_ID = #{item.parentId,jdbcType=INTEGER}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE, VALID_FLAG = #{item.validFlag,jdbcType=INTEGER} where LAN_ID = #{item.regId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT INTO SYS_LANGUAGE_T (LAN_ID, LAN_CODE, LAN_TYPE, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_LANGUAGE_S.NEXTVAL, #{item.lanCode,jdbcType=VARCHAR}, #{item.lanType,jdbcType=INTEGER}, #{item.attribute1,jdbcType=VARCHAR}, #{item.attribute2,jdbcType=VARCHAR}, #{item.attribute3,jdbcType=VARCHAR}, #{item.attribute4,jdbcType=VARCHAR}, #{item.attribute5,jdbcType=VARCHAR}, #{item.attribute6,jdbcType=VARCHAR}, #{item.attribute7,jdbcType=VARCHAR}, #{item.attribute8,jdbcType=VARCHAR}, #{item.attribute9,jdbcType=VARCHAR}, #{item.attribute10,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
