mapper.select:
SELECT

FROM SYS_LOOKUP_CLASSIFY_T T1 
where T1.VALID_FLAG=1 AND T1.CLASS_ID = #{classId,jdbcType=NUMERIC}

mapper.update:
declare
begin
UPDATE SYS_LOOKUP_CLASSIFY_T SET CLASS_NAME = #{item.className,jdbcType=VARCHAR}, CLASS_CODE = #{item.classCode,jdbcType=VARCHAR}, CLASS_DESC = #{item.classDesc,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where CLASS_ID = #{item.classId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_LOOKUP_CLASSIFY_T ( CLASS_ID, CLASS_NAME, CLASS_CODE, CLASS_DESC, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_LOOKUP_CLASSIFY_S.NEXTVAL, #{item.className,jdbcType=VARCHAR}, #{item.classCode,jdbcType=VARCHAR}, #{item.classDesc,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
