mapper.update:
declare
begin
UPDATE SYS_LOOKUP_CLASSIFY_T SET CLASS_NAME = #{item.className,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=DECIMAL}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=DECIMAL}, UPDATE_LAST_DATE = #{item.updateLastDate,jdbcType=DATE}, VALID_FLAG = #{item.validFlag,jdbcType=DECIMAL} where CLASS_ID = #{item.classId,jdbcType=VARCHAR};
end;

mapper.insert:
declare
begin
INSERT into SYS_LOOKUP_CLASSIFY_T (CLASS_ID, CLASS_NAME, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG) values ( #{item.classId,jdbcType=VARCHAR}, #{item.className,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=DECIMAL}, #{item.createdUser,jdbcType=DECIMAL}, #{item.createdDate,jdbcType=DATE}, #{item.updateLastUser,jdbcType=DECIMAL}, #{item.updateLastDate,jdbcType=DATE}, #{item.validFlag,jdbcType=DECIMAL});
end;
