mapper.update:
declare
begin
UPDATE SYS_LOOKUP_ITEM_T set ITEM_NAME = #{item.itemName,jdbcType=VARCHAR}, CLASS_ID = #{item.classId,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=DECIMAL}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=DECIMAL}, UPDATE_LAST_DATE = #{item.updateLastDate,jdbcType=DATE}, VALID_FLAG = #{item.validFlag,jdbcType=DECIMAL} where ITEM_ID = #{item.itemId,jdbcType=VARCHAR};
end;

mapper.insert:
declare
begin
INSERT into SYS_LOOKUP_ITEM_T ( ITEM_ID, ITEM_NAME, CLASS_ID, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( #{item.itemId,jdbcType=VARCHAR}, #{item.itemName,jdbcType=VARCHAR}, #{item.classId,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=DECIMAL}, #{item.createdUser,jdbcType=DECIMAL}, #{item.createdDate,jdbcType=DATE}, #{item.updateLastUser,jdbcType=DECIMAL}, #{item.updateLastDate,jdbcType=DATE}, #{item.validFlag,jdbcType=DECIMAL} );
end;
