mapper.select:
SELECT

FROM SYS_LOOKUP_ITEM_T T1
where VALID_FLAG=1 AND T1.ITEM_ID = #{itemId,jdbcType=NUMERIC}

mapper.update:
declare
begin
UPDATE SYS_LOOKUP_ITEM_T set ITEM_NAME = #{item.itemName,jdbcType=VARCHAR}, CLASS_ID = #{item.classId,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where ITEM_ID = #{item.itemId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_LOOKUP_ITEM_T ( ITEM_ID, ITEM_NAME, CLASS_ID, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_LOOKUP_ITEM_S.NEXTVAL, #{item.itemName,jdbcType=VARCHAR}, #{item.classId,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
