mapper.select:
SELECT

FROM SYS_LOOKUP_ITEM_T T1 
where VALID_FLAG=1 AND T1.ITEM_ID = #{itemId,jdbcType=INTEGER}

SELECT

FROM SYS_LOOKUP_ITEM_T T1
where T1.VALID_FLAG=1 AND T1.CLASS_ID = #{classId,jdbcType=INTEGER}

mapper.update:
declare
begin
UPDATE SYS_LOOKUP_ITEM_T set ITEM_NAME = #{item.itemName,jdbcType=VARCHAR}, ITEM_CODE = #{item.classId,jdbcType=VARCHAR}, ITEM_LAN = #{item.itemLan,jdbcType=INTEGER}, ITEM_STATUS = #{item.itemStatus,jdbcType=INTEGER}, REMARKS = #{item.remarks,jdbcType=VARCHAR}, CLASS_ID = #{item.classId,jdbcType=INTEGER}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where ITEM_ID = #{item.itemId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_LOOKUP_ITEM_T ( ITEM_ID, ITEM_CODE, ITEM_NAME, ITEM_LAN, CLASS_ID, ITEM_STATUS, REMARKS, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_LOOKUP_ITEM_S.NEXTVAL, #{item.itemCode,jdbcType=VARCHAR}, #{item.itemName,jdbcType=VARCHAR}, #{item.itemLan,jdbcType=INTEGER}, #{item.classId,jdbcType=INTEGER}, #{item.itemStatus,jdbcType=INTEGER}, #{item.remarks,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
