mapper.update:
declare
begin
UPDATE SYS_REGISTER_T set REG_NAME = #{item.regName,jdbcType=VARCHAR}, REG_CODE = #{item.regCode,jdbcType=VARCHAR}, REG_DESC = #{item.regDesc,jdbcType=VARCHAR}, PARENT_ID = #{item.parentId,jdbcType=INTEGER}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=DECIMAL}, UPDATE_LAST_DATE = #{item.updateLastDate,jdbcType=DATE}, VALID_FLAG = #{item.validFlag,jdbcType=INTEGER} where REG_ID = #{regId,jdbcType=DECIMAL};
end;

mapper.insert:
declare
begin
INSERT into SYS_REGISTER_T (REG_ID, REG_NAME, REG_CODE, REG_DESC, PARENT_ID, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG) values ( SYS_REGISTER_S.NEXTVAL, #{item.regName,jdbcType=VARCHAR}, #{item.regCode,jdbcType=VARCHAR}, #{item.regDesc,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=DECIMAL}, #{item.createdDate,jdbcType=DATE}, #{item.updateLastUser,jdbcType=DECIMAL}, #{item.updateLastDate,jdbcType=DATE}, #{validFlag,jdbcType=INTEGER});
end;
