mapper.select:
SELECT

FROM SYS_REGISTER_T T1 
where T1.VALID_FLAG=1 AND T1.PARENT_PATH = #{parentPath,jdbcType=VARCHAR} || #{regCode,jdbcType=VARCHAR}

SELECT

FROM SYS_REGISTER_T T1 
where T1.VALID_FLAG=1 AND T1.PARENT_ID = #{regId,jdbcType=INTEGER}

SELECT

FROM SYS_REGISTER_T T1 
where T1.VALID_FLAG=1 AND T1.REG_ID = #{regId,jdbcType=INTEGER}

mapper.update:
declare
begin
UPDATE SYS_REGISTER_T SET REG_NAME = #{item.regName,jdbcType=VARCHAR}, REG_CODE = #{item.regCode,jdbcType=VARCHAR}, REG_DESC = #{item.regDesc,jdbcType=VARCHAR}, REG_VALUE = #{item.regValue,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where REG_ID = #{item.regId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_REGISTER_T ( REG_ID, REG_NAME, REG_CODE, REG_DESC, REG_VALUE, PARENT_ID, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_REGISTER_S.NEXTVAL, #{item.regName,jdbcType=VARCHAR}, #{item.regCode,jdbcType=VARCHAR}, #{item.regDesc,jdbcType=VARCHAR}, #{item.regValue,jdbcType=VARCHAR}, #{item.parentId,jdbcType=NUMERIC}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
