mapper.select:
SELECT

FROM SYS_AUTHORITIES_T T1 
where T1.VALID_FLAG=1 AND T1.AUTH_ID = #{authId,jdbcType=NUMERIC}

mapper.update:
declare
begin
UPDATE SYS_AUTHORITIES_T SET AUTH_NAME = #{item.authName,jdbcType=VARCHAR}, AUTH_DESC = #{item.authDesc,jdbcType=VARCHAR}, ENABLED = #{item.enabled,jdbcType=INTEGER}, ISSYS = #{item.issys,jdbcType=INTEGER}, ATTRIBUTE1 = #{item.attribute1,jdbcType=VARCHAR}, ATTRIBUTE2 = #{item.attribute2,jdbcType=VARCHAR}, ATTRIBUTE3 = #{item.attribute3,jdbcType=VARCHAR}, ATTRIBUTE4 = #{item.attribute4,jdbcType=VARCHAR}, ATTRIBUTE5 = #{item.attribute5,jdbcType=VARCHAR}, ATTRIBUTE6 = #{item.attribute6,jdbcType=VARCHAR}, ATTRIBUTE7 = #{item.attribute7,jdbcType=VARCHAR}, ATTRIBUTE8 = #{item.attribute8,jdbcType=VARCHAR}, ATTRIBUTE9 = #{item.attribute9,jdbcType=VARCHAR}, ATTRIBUTE10 = #{item.attribute10,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where AUTH_ID = #{item.authId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_AUTHORITIES_T ( AUTH_ID, AUTH_NAME, AUTH_DESC, ENABLED, ISSYS, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_ROLES_S.NEXTVAL, #{item.authName,jdbcType=VARCHAR}, #{item.authDesc,jdbcType=VARCHAR}, #{item.enabled,jdbcType=INTEGER}, #{item.issys,jdbcType=INTEGER}, #{item.attribute1,jdbcType=VARCHAR}, #{item.attribute2,jdbcType=VARCHAR}, #{item.attribute3,jdbcType=VARCHAR}, #{item.attribute4,jdbcType=VARCHAR}, #{item.attribute5,jdbcType=VARCHAR}, #{item.attribute6,jdbcType=VARCHAR}, #{item.attribute7,jdbcType=VARCHAR}, #{item.attribute8,jdbcType=VARCHAR}, #{item.attribute9,jdbcType=VARCHAR}, #{item.attribute10,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
