mapper.select:

 WHERE 1 = 1

	AND T1.PARENT_ID = #{parentId,jdbcType=NUMERIC}

ORDER BY T1.ORDER_CODE ASC

mapper.update:
declare
begin
UPDATE SYS_RESOURCES_T SET RESOURCE_NAME = #{resourceName,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=NUMERIC}, ICON = #{icon,jdbcType=VARCHAR}, RES_URL = #{resUrl,jdbcType=VARCHAR}, enabled = #{enabled,jdbcType=INTEGER}, RESOURCE_DESC = #{resourceDesc,jdbcType=VARCHAR}, RESOURCE_TYPE = #{resourceType,jdbcType=VARCHAR}, ORDER_CODE = #{orderCode,jdbcType=NUMERIC}, UPDATE_LAST_USER = #{updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE WHERE RESOURCE_ID = #{resourceId,jdbcType=NUMERIC}
end;

mapper.insert:
declare
begin
insert into SYS_RESOURCES_T ( RESOURCE_ID, RESOURCE_NAME, PARENT_ID, ICON, RES_URL, resource_desc, RESOURCE_TYPE, enabled, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE ) values ( SYS_RESOURCES_S.NEXTVAL, #{item.resourceName,jdbcType=VARCHAR}, #{item.parentId,jdbcType=NUMERIC}, #{item.icon,jdbcType=VARCHAR}, #{item.resUrl,jdbcType=VARCHAR}, #{item.resourceDesc,jdbcType=VARCHAR}, #{item.resourceType,jdbcType=INTEGER}, #{item.enabled,jdbcType=INTEGER}, #{item.orderCode,jdbcType=NUMERIC}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE );
end;
